import '../cardInfo/styles.css';
import { useParams } from 'react-router-dom';
import { useState } from 'react';
import '../textsFormate/formate.css';
import projects from '../../json/projects.json';

export default function CardInfo() {
    const { id } = useParams();
    const projeto = projects.find((p) => p.id === Number(id));
    const [showImages, setShowImages] = useState(false);
    const [currentIndex, setCurrentIndex] = useState(0);

    if (!projeto) {
        return <p>Projeto n√£o encontrado</p>;
    }

    const handleNext = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % projeto.imagens.length);
    };

    const handlePrev = () => {
        setCurrentIndex((prevIndex) => (prevIndex - 1 + projeto.imagens.length) % projeto.imagens.length);
    };

    return (
        <div className="publiTitle">
            <p className="subtitulo">PROJETOS</p>
            <div className="projeto-conteudo-container">
                <div className="projeto-conteudo-info">
                    <div className="project-img-container">
                        {showImages ? (
                            <div className="carousel">
                                <button className="carousel-button" onClick={handlePrev}>&#8249;</button>
                                <img src={projeto.imagens[currentIndex]} alt={`Imagem ${currentIndex + 1}`} className="carousel-image" />
                                <button className="carousel-button" onClick={handleNext}>&#8250;</button>
                            </div>
                        ) : (
                            <div className="project-button-container">
                                <button className="project-img-button" onClick={() => setShowImages(true)}>Ver imagens</button>
                            </div>
                        )}
                    </div>
                    <div className="info">
                        <div className="info-description">
                            <div className="info-container">
                                <div className="category">{projeto.classificacao}</div>
                                <div className="status">{projeto.status}</div>
                            </div>
                            <div className="info-projeto">
                                <p className="subtitulo themePurple">{projeto.nome}</p>
                                <p className="subtitulo-post themePurple">Desenvolvido por: {projeto.desenvolvedores.map((dev, index) => (
                                    <span key={index} className="themeOrange">{dev}{index !== projeto.desenvolvedores.length - 1 ? ', ' : ''}</span>
                                ))}</p>
                            </div>
                        </div>
                        <div className="info-acess">
                            <a className="acess-button" href={projeto.link} target="_blank" rel="noopener noreferrer">Acessar projeto</a>
                            <a className="share-button">
                                <i className="fi fi-rr-share"></i> Share
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
