import { BrowserRouter, Route, Routes, Navigate } from 'react-router-dom';
import SobreMim from './pages/sobreMim';
import Projetos from './pages/Projetos';
import Blog from './pages/blog';
import PaginaPadrao from './pages/PaginaPadrao/index';
import ProjetosDetalhes from './pages/Projeto-details';
import Contato from './pages/Contato';
import ScrollToTop from './componentes/Scroll/scroll';

function AppRoutes() {
  // Define o basename apenas para produção e se PUBLIC_URL estiver definido
  const basename = process.env.NODE_ENV === 'production' ? (process.env.PUBLIC_URL || '') : '';

  return (
    <BrowserRouter basename={basename}>
      <ScrollToTop />

      <Routes>
        {/* Rota principal com layout padrão */}
        <Route path="/" element={<PaginaPadrao />}>
          <Route index element={<SobreMim />} />
          <Route path="projetos" element={<Projetos />} />
          <Route path="projetos/:id" element={<ProjetosDetalhes />} />
          <Route path="contato" element={<Contato />} />
          <Route path="blog" element={<Blog />} />
        </Route>

        {/* Redirecionamento para páginas antigas */}
        <Route path="/sobre" element={<Navigate to="/" replace />} />

        {/* Página 404 personalizada */}
        <Route path="*" element={
          <PaginaPadrao>
            <div className="container">
              <h1>Página não encontrada</h1>
              <p>A URL solicitada não existe</p>
            </div>
          </PaginaPadrao>
        } />
      </Routes>
    </BrowserRouter>
  );
}

export default AppRoutes;
