import React from 'react';
import { NavLink } from "react-router"
import styles from '../menuLink/MenuLink.module.css'; // Importação do CSS Module

export default function MenuLink({ children, to }) {

    return (
        <NavLink
            className={({ isActive }) => `
                ${styles.link}
                ${isActive ? styles.linkDestacado : ""}
            `}
            to={to}
            end
        >
            {children.children}
        </NavLink>
    )
}
