import React, { useState } from "react";
import "./style.css";

const ShareCard = ({ link }) => {
  const [showShareOptions, setShowShareOptions] = useState(false);

  const shareOnSocial = (platform) => {
    const urls = {
      facebook: `https://www.facebook.com/sharer/sharer.php?u=${link}`,
      twitter: `https://twitter.com/intent/tweet?url=${link}`,
      linkedin: `https://www.linkedin.com/shareArticle?url=${link}`,
    };

    window.open(urls[platform], "_blank");
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(link);
    alert("Link copiado!");
  };

  return (
    <div>
      <button className="share-btn" onClick={() => setShowShareOptions(true)}>
        Compartilhar
      </button>

      {showShareOptions && (
        <div className="overlay">
          <div className="share-card">
            <button className="close-btn" onClick={() => setShowShareOptions(false)}>Ã—</button>
            <h3>Compartilhar</h3>
            <div className="share-options">
              <button onClick={() => shareOnSocial("facebook")}>
                <i className="fi fi-brands-facebook"></i> Facebook
              </button>
              <button onClick={() => shareOnSocial("twitter")}>
                <i className="fi fi-brands-twitter"></i> Twitter
              </button>
              <button onClick={() => shareOnSocial("linkedin")}>
                <i className="fi fi-brands-linkedin"></i> LinkedIn
              </button>
              <button onClick={copyToClipboard}>
                <i className="fi fi-rr-copy"></i> Copiar link
              </button>
            </div>
            <input type="text" value={link} readOnly />
          </div>
        </div>
      )}
    </div>
  );
};

export default ShareCard;
