import { useState, useEffect } from "react";
import "../navegacaoProjetos/navegacaoProjetos.css";
import "../textsFormate/formate.css";
import projects from "../../json/projects.json";
import { Link } from "react-router-dom";
import AOS from "aos";
import "aos/dist/aos.css";

function NavegacaoProjetos() {
    const [categoriaAtiva, setCategoriaAtiva] = useState("Todos");
    const [paginaAtual, setPaginaAtual] = useState(1);
    const itensPorPagina = 6;

    useEffect(() => {
        AOS.init({
            duration: 1000,
            once: true,
            disable: window.innerWidth < 768,
        });
    }, []);

    const handleFiltro = (categoria) => {
        setCategoriaAtiva(categoria);
        setPaginaAtual(1); // Reseta para a primeira página ao mudar o filtro
    };

    const projetosFiltrados = categoriaAtiva === "Todos"
        ? projects
        : projects.filter(projeto => projeto.categoria === categoriaAtiva);

    const totalPaginas = Math.ceil(projetosFiltrados.length / itensPorPagina);
    const indiceInicio = (paginaAtual - 1) * itensPorPagina;
    const projetosPaginados = projetosFiltrados.slice(indiceInicio, indiceInicio + itensPorPagina);

    const mudarPagina = (novaPagina) => {
        if (novaPagina >= 1 && novaPagina <= totalPaginas) {
            setPaginaAtual(novaPagina);
        }
    };

    return (
        <div className="main-container">
            <div className="projetos-container">
                <nav className="projetos-nav">
                    {['Todos', 'Design', 'Code', 'Dashboards', 'Hackathons'].map((categoria) => (
                        <button 
                            key={categoria}
                            className={`projetos-nav-btn ${categoriaAtiva === categoria ? 'ativo' : ''}`}
                            onClick={() => handleFiltro(categoria)}
                            data-aos="fade-up"
                            data-aos-delay="100"
                        >
                            {categoria}
                        </button>
                    ))}
                </nav>

                <div className="cards-container">
                    {projetosPaginados.map((projeto) => (
                        <Link
                            to={`/projetos/${projeto.id}`}
                            key={projeto.id}
                            className="projeto-card"
                            style={{
                                backgroundImage: `url(/assets/${projeto.id}/capa.png)`,
                                backgroundSize: 'cover',
                                backgroundPosition: 'center',
                            }}
                            data-aos="zoom-in"
                            data-aos-delay="200"
                        >
                        </Link>
                    ))}
                </div>

                {totalPaginas > 1 && (
                    <div className="paginacao">
                        <button onClick={() => mudarPagina(paginaAtual - 1)} disabled={paginaAtual === 1}>
                            Anterior
                        </button>
                        {Array.from({ length: totalPaginas }, (_, i) => (
                            <button
                                key={i + 1}
                                onClick={() => mudarPagina(i + 1)}
                                className={paginaAtual === i + 1 ? "ativo" : ""}
                            >
                                {i + 1}
                            </button>
                        ))}
                        <button onClick={() => mudarPagina(paginaAtual + 1)} disabled={paginaAtual === totalPaginas}>
                            Próximo
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
}

export default NavegacaoProjetos;
