import React, { useState } from "react";
import "../forms/styles.css"; // Importando o arquivo CSS

const Form = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    textarea: "",
  });

  const [status, setStatus] = useState(null); // Para feedback ao usuário

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = {
      name: formData.name,
      email: formData.email,
      message: formData.textarea,
    };

    try {
      const response = await fetch("https://formsubmit.co/giolaucm.dev@gmail.com", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify(formDataToSend),
      });

      if (response.ok) {
        setStatus("Mensagem enviada com sucesso!");
        setFormData({ name: "", email: "", textarea: "" });
      } else {
        setStatus("Algo deu errado. Tente novamente.");
      }
    } catch (error) {
      console.error("Erro ao enviar:", error);
      setStatus("Erro na conexão. Tente mais tarde.");
    }
  };

  return (
    <div className="form-container">
      <form className="form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Nome</label>
          <input
            type="text"
            id="name"
            name="name"
            required
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">E-mail</label>
          <input
            type="email"
            id="email"
            name="email"
            required
            value={formData.email}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="textarea">Como posso te ajudar?</label>
          <textarea
            name="textarea"
            id="textarea"
            rows={10}
            cols={50}
            required
            value={formData.textarea}
            onChange={handleChange}
          />
        </div>
        <button className="form-submit-btn" type="submit">
          Enviar
        </button>
        {status && <p className="form-status">{status}</p>}
      </form>
    </div>
  );
};

export default Form;
