import './style.css';
import "../textsFormate/formate.css";
import background1 from "../img/exemplo1.jpg";
import background2 from "../img/exemplo2.jpg";
import background3 from "../img/exemplo3.jpg";
import background4 from "../img/exemplo3.jpg";
import background5 from "../img/exemplo3.jpg";
import { Link } from "react-router-dom";


export default function BlogCards() {
    return (
        <div className="blog-container">
            {BlogData.map((blog) => (
                <Link to="/"
                    key={blog.id}
                    className="blog-card"
                    style={{
                        backgroundImage: `url(${process.env.PUBLIC_URL + blog.background})`, // Caminho para imagens na pasta public
                        backgroundSize: "cover", // Cobrir todo o card
                        backgroundPosition: "center" // Centralizar imagem
                    }}
                >

                    <div className="blog-card-classificacao">
                        <p className="classification themePurple">{blog.classification}</p>
                    </div>
                    <div className="blog-card-data">
                        <p className="date themePurple">{blog.date}</p>
                    </div>
                    <div className="blog-card-titulo">
                        <p className="titulo-card themePurple">{blog.name}</p>
                    </div>
                    <div className="descricao-card projeto-card-descricao">
                        <p className="descricao">{blog.description}</p>
                    </div>
                </Link>
            ))}
        </div>
    );
}

const blogData = [
  {
    id: 1,
    name: "Projeto 1",
    classification: "Dashboard",
    date: "01/01/2021",
    description: "Descrição do Projeto 1",
    background: background1
  },
  {
    id: 2,
    name: "Projeto 2",
    classification: "Figma",
    date: "01/01/2021",
    description: "Descrição do Projeto 2",
    background: background2
  },
  {
    id: 3,
    name: "Projeto 3",
    date: "01/01/2021",
    classification: "Web Design",
    description: "Descrição do Projeto 3",
    background: background3
  },
  {
    id: 4,
    name: "Projeto 3",
    classification: "Web Design",
    date: "01/01/2021",
    description: "Descrição do Projeto 3",
    background: background4
  },
  {
    id: 5,
    name: "Projeto 3",
    date: "01/01/2021",
    classification: "Web Design",
    description: "Descrição do Projeto 3",
    background: background5
  }
];
