import React, { useState } from "react";
import "../forms/styles.css";
import Notificacao from "../alert/index"; // ajuste o caminho conforme seu projeto
import imgCopy from "../img/not_copy.svg"; // use outra imagem se quiser

const Form = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    textarea: "",
  });

  const [status, setStatus] = useState(null);
  const [notificacaoVisivel, setNotificacaoVisivel] = useState(false);
  const [mensagemNotificacao, setMensagemNotificacao] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    formDataToSend.append("name", formData.name);
    formDataToSend.append("email", formData.email);
    formDataToSend.append("message", formData.textarea);

    try {
      const response = await fetch("https://formsubmit.co/giolaucm.dev@gmail.com", {
        method: "POST",
        body: formDataToSend,
      });

      if (response.ok) {
        setFormData({ name: "", email: "", textarea: "" });
        setStatus("Mensagem enviada com sucesso!");
        setMensagemNotificacao("Mensagem enviada com sucesso!");
        setNotificacaoVisivel(true);
      } else {
        setStatus("Algo deu errado. Tente novamente.");
        setMensagemNotificacao("Erro ao enviar. Tente novamente.");
        setNotificacaoVisivel(true);
      }
    } catch (error) {
      console.error("Erro ao enviar:", error);
      setStatus("Erro na conex達o. Tente mais tarde.");
      setMensagemNotificacao("Erro na conex達o. Tente mais tarde.");
      setNotificacaoVisivel(true);
    }
  };

  return (
    <div className="form-container">
      <form className="form" onSubmit={handleSubmit}>
        {/* ... seus inputs */}
        <button className="form-submit-btn" type="submit">
          Enviar
        </button>
        {status && <p className="form-status">{status}</p>}
      </form>

      <Notificacao
        visivel={notificacaoVisivel}
        onFechar={() => setNotificacaoVisivel(false)}
        mensagem={mensagemNotificacao}
        imagem={imgCopy}
      />
    </div>
  );
};

export default Form;
