import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import styles from "../Menu/Menu.module.css";
import Logo from "../img/logo.svg";
import MenuLink from "../MenuLink";
import translations from "../../translations/translations";
import FlagPT from "../img/flags/brasil.png";
import FlagUS from "../img/flags/estados-unidos.png";
import FlagES from "../img/flags/spain.png";

function Header() {
  const location = useLocation();
  const [isScrolled, setIsScrolled] = useState(false);
  const [language, setLanguage] = useState(
    localStorage.getItem("language") || "pt"
  );
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const menuRef = useRef(null);

  const languages = {
    pt: { label: "PT", flag: FlagPT },
    en: { label: "EN", flag: FlagUS },
    es: { label: "ES", flag: FlagES },
  };

  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 50);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    if (location.pathname === "/projetos") {
      setHeaderColor(styles.projectsColor);
    } else if (location.pathname === "/contato") {
      setHeaderColor(styles.contactColor);
    } else {
      setHeaderColor("");
    }
  }, [location.pathname]);

  const handleLanguageChange = (event) => {
    const selectedLanguage = event.target.value;
    setLanguage(selectedLanguage);
    localStorage.setItem("language", selectedLanguage);
  };
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Fecha o menu ao redimensionar a tela
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth > 768) {
        setIsMenuOpen(false);
      }
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <header className={`${styles.header} ${isScrolled ? styles.scrolled : ""}`}>
      <div className={styles.logo}>
        <img src={Logo} alt="Logo" />
      </div>

    {/* Botão Hambúrguer */}
      <button 
        className={`${styles.menuButton} ${isMenuOpen ? styles.open : ''}`}
        onClick={() => setIsMenuOpen(!isMenuOpen)}
        aria-label="Menu"
        aria-expanded={isMenuOpen}
      >
        <span></span>
        <span></span>
        <span></span>
      </button>

    {/* Menu de Navegação */}
      <nav 
        className={`${styles.navbar} ${isMenuOpen ? styles.active : ''}`} 
        ref={menuRef}
        aria-hidden={!isMenuOpen}
      >
        <ul>
          <li>
            <MenuLink
              to="/"
              label={translations[language].sobre}
              isActive={location.pathname === "/"}
            />
          </li>
          <li>
            <MenuLink
              to="/projetos"
              label={translations[language].projetos}
              isActive={location.pathname === "/projetos"}
              className={styles.headerColor}
            />
          </li>
          <li>
            <MenuLink
              to="/contato"
              label={translations[language].contato}
              className={`${styles.contact} ${headerColor}`}
            />
          </li>
        </ul>

        <div className={styles.languageSelector}>
          <img
            src={languages[language].flag}
            style={{ width: "40px", height: "40px" }}
            alt="Bandeira"
          />
          <select 
            value={language} 
            onChange={handleLanguageChange}
            aria-label="Seletor de idioma"
          >
            {Object.keys(languages).map((key) => (
              <option key={key} value={key}>
                {languages[key].label}
              </option>
            ))}
          </select>
        </div>
      </nav>
    </header>
  );
}

export default Header;
