import './style.css';
import "../textsFormate/formate.css";
import background1 from "../img/exemplo1.jpg";
import background2 from "../img/exemplo2.jpg";
import background3 from "../img/exemplo3.jpg";
import background4 from "../img/exemplo3.jpg";
import background5 from "../img/exemplo3.jpg";
import { Link } from "react-router-dom";


export default function BlogCards() {
    return (
        <div className="blog-card-container">
        {blogData.map((blog) => (
            <Link
                to="/"
                key={blog.id}
                className="blog-card"
                style={{
                    backgroundImage: `url(${process.env.PUBLIC_URL + blog.background})`,
                    backgroundSize: "cover",
                    backgroundPosition: "center",
                }}
            >
                {/* Container para a camada de informações */}
                <div className="blog-card-content">

                  
                  {/*<div className="blog-card-info">
                      {Data }
                      <p className="blog-card-date">{blog.date}</p>
    
                      {Título}
                      <h3 className="blog-card-title">{blog.name}</h3>

                      { Descrição}
                      <p className="blog-card-description">{blog.description}</p>
                  </div>*/}
                 
                  </div>

                  <div class="card">
                    <img class="card-img-top" src="..." alt="Card image cap"/>
                    <div class="card-body">
                      <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                    </div>
                  
                </div>
            </Link>
        ))}
    </div>
    
    );
}

const blogData = [
  {
    id: 1,
    name: "Projeto 1",
    classification: "Dashboard",
    date: "01/01/2021",
    description: "Descrição do Projeto 1",
    background: background1
  },
  {
    id: 2,
    name: "Projeto 2",
    classification: "Figma",
    date: "01/01/2021",
    description: "Descrição do Projeto 2",
    background: background2
  },
  {
    id: 3,
    name: "Projeto 3",
    date: "01/01/2021",
    classification: "Web Design",
    description: "Descrição do Projeto 3",
    background: background3
  },
  {
    id: 4,
    name: "Projeto 3",
    classification: "Web Design",
    date: "01/01/2021",
    description: "Descrição do Projeto 3",
    background: background4
  },
  {
    id: 5,
    name: "Projeto 3",
    date: "01/01/2021",
    classification: "Web Design",
    description: "Descrição do Projeto 3",
    background: background5
  }
];
