import { useState } from "react";
import "../navegacaoProjetos/navegacaoProjetos.css";

const projetos = [
    { id: 1, titulo: "Projeto Design", descricao: "Descrição do projeto", categoria: "Design" },
    { id: 2, titulo: "Projeto Code", descricao: "Descrição do projeto", categoria: "Code" },
    { id: 3, titulo: "Projeto Dashboard", descricao: "Descrição do projeto", categoria: "Dashboards" },
    { id: 4, titulo: "Projeto Hackathon", descricao: "Descrição do projeto", categoria: "Hackathons" },
    { id: 5, titulo: "Projeto Full", descricao: "Descrição do projeto", categoria: "Todos" },
];

function NavegacaoProjetos() {
    const [categoriaAtiva, setCategoriaAtiva] = useState("Todos");

    const handleFiltro = (categoria) => {
        setCategoriaAtiva(categoria);
    };

    const projetosFiltrados = categoriaAtiva === "Todos" 
        ? projetos 
        : projetos.filter(projeto => projeto.categoria === categoriaAtiva);

    return (
        <div>
            <nav className="projetos-nav">
                {['Todos', 'Design', 'Code', 'Dashboards', 'Hackathons'].map((categoria) => (
                    <button 
                        key={categoria} 
                        className={`projetos-nav-btn ${categoriaAtiva === categoria ? 'ativo' : ''}`}
                        onClick={() => handleFiltro(categoria)}
                    >
                        {categoria}
                    </button>
                ))}
            </nav>
            
            <div className="cards-container">
                {projetosFiltrados.map((projeto) => (
                    <div key={projeto.id} className="projeto-card">
                        <h2 className="titulo-card">{projeto.titulo}</h2>
                        <p className="descricao-card">{projeto.descricao}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default NavegacaoProjetos;
