import './style.css';
import "../textsFormate/formate.css";
import background1 from "../img/exemplo1.jpg";
import background2 from "../img/exemplo2.jpg";
import background3 from "../img/exemplo3.jpg";
import background4 from "../img/exemplo3.jpg";
import background5 from "../img/exemplo3.jpg";
import { Link } from "react-router-dom";

export default function BlogCards() {
    return (
        <div className="blog-card-container d-flex flex-wrap justify-content-center">
            {blogData.map((blog) => (
                <div key={blog.id} className="m-3">
                    <Link
                        to="/"
                        className="text-decoration-none"
                        style={{
                            color: "inherit",
                        }}
                    >
                        <div className="card" style={{ width: "18rem" }}>
                            <img
                                className="card-img-top"
                                src={blog.background}
                                alt={blog.name}
                                style={{ height: "200px", objectFit: "cover" }}
                            />
                            <div className="card-body">
                                <p className="blog-card-date text-muted mb-1">
                                    {blog.date}
                                </p>
                                <h3 className="blog-card-title card-title">
                                    {blog.name}
                                </h3>
                                <p className="card-text blog-card-description">
                                    {blog.description}
                                </p>
                            </div>
                        </div>
                    </Link>
                </div>
            ))}
        </div>
    );
}

const blogData = [
    {
        id: 1,
        name: "Projeto 1",
        classification: "Dashboard",
        date: "01/01/2021",
        description: "Descrição do Projeto 1",
        background: background1,
    },
    {
        id: 2,
        name: "Projeto 2",
        classification: "Figma",
        date: "01/01/2021",
        description: "Descrição do Projeto 2",
        background: background2,
    },
    {
        id: 3,
        name: "Projeto 3",
        classification: "Web Design",
        date: "01/01/2021",
        description: "Descrição do Projeto 3",
        background: background3,
    },
    {
        id: 4,
        name: "Projeto 4",
        classification: "Web Design",
        date: "01/01/2021",
        description: "Descrição do Projeto 4",
        background: background4,
    },
    {
        id: 5,
        name: "Projeto 5",
        classification: "Web Design",
        date: "01/01/2021",
        description: "Descrição do Projeto 5",
        background: background5,
    },
];
