import React, { useEffect, useRef, useState } from 'react';
import { useLocation } from 'react-router-dom';
import styles from '../Menu/Menu.module.css'; // Importação do CSS Module
import Logo from '../img/logo.svg';
import MenuLink from '../MenuLink'; // Importação do componente MenuLink

function Header() {
  const location = useLocation(); // Obtém a rota atual
  const [isScrolled, setIsScrolled] = useState(false); // Estado para controlar o scroll
  const headerRef = useRef(null); // Ref para o elemento header

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 100) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll); // Adiciona o listener de scroll

    return () => {
      window.removeEventListener('scroll', handleScroll); // Remove o listener ao desmontar o componente
    };
  }, []);

  return (
    <header
      ref={headerRef} // Atribui a ref ao header
      className={`${styles.header} ${isScrolled ? styles.scrolled : ''}`}
    >
      <div className={styles.logo}>
        <img src={Logo} alt="Logo" />
      </div>
      <nav className={styles.navbar}>
        <ul>
          <li>
            <MenuLink
              to="/"
              label="SOBRE"
              isActive={location.pathname === '/'}
            />
          </li>
          <li>
            <MenuLink
              to="/projetos"
              label="PROJETOS"
              isActive={location.pathname === '/projetos'}
            />
          </li>
          <li>
            <MenuLink
              to="/blog"
              label="BLOG"
              isActive={location.pathname === '/blog'}
            />
          </li>
          <li>
            <MenuLink
              to="/contato"
              label="CONTATO"
              isActive={location.pathname === '/contato'}
            />
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
