import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import SobreMim from './pages/sobreMim';
import Projetos from './pages/Projetos';
import Blog from './pages/blog';
import Menu from './componentes/Menu/Menu';
import PaginaPadrao from './pages/PaginaPadrao/index';

function App() {
  const location = useLocation();

  // Determina o fundo com base na rota
  const backgroundStyle =
    location.pathname === '/'
      ? { backgroundImage: `url('../img/background.png')` } // Fundo para a Home
      : location.pathname === '/projetos'
      ? { backgroundImage: `url('../img/background-projects.png')` } // Fundo para Projetos
      : { backgroundImage: `url('../img/background-default.png')` }; // Fundo padrão para outras rotas

  return (
    <div style={{ ...backgroundStyle, backgroundRepeat: 'no-repeat', backgroundSize: 'cover', backgroundPosition: 'center' }}>
      <Router>
        <Menu /> {/* O menu permanece fixo em todas as rotas */}
        <Routes>
          <Route path="/" element={<PaginaPadrao />}>
            <Route index element={<SobreMim />} />
            <Route path="/projetos" element={<Projetos />} />
            <Route path="/blog" element={<Blog />} />
          </Route>
          <Route path="*" element={<h1>Not Found</h1>} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
