import React, { useState } from 'react';
import styles from '../Menu/Menu.module.css'; // Importando o CSS corretamente como "styles"
import { Link } from 'react-router-dom'; // Importando Link
import Logo from '../img/logo.svg';

function Header() {
  const [activeTab, setActiveTab] = useState("sobre");

  return (
    <header className={styles.header}> {/* Usando o estilo importado */}
      <div className={styles.logo}> {/* Usando o estilo importado */}
        <img src={Logo} alt="Logo" />
      </div>
      <nav className={styles.navbar}> {/* Usando o estilo importado */}
        <Link
          to="/"
          className={`${styles.link} ${activeTab === "sobre" ? styles.active : ""}`}
          onClick={() => setActiveTab("sobre")}
        >
          SOBRE
        </Link>
        <Link
          to="/projetos"
          className={`${styles.link} ${activeTab === "projetos" ? styles.active : ""}`}
          onClick={() => setActiveTab("projetos")}
        >
          PROJETOS
        </Link>
        <Link
          to="/blog"
          className={`${styles.link} ${activeTab === "blog" ? styles.active : ""}`}
          onClick={() => setActiveTab("blog")}
        >
          BLOG
        </Link>
      </nav>
    </header>
  );
}

export default Header;
