import React from 'react';
import { NavLink } from 'react-router-dom';
import styles from './menuLink.module.css';

function MenuLink({ to, label }) {
  const isContato = label.toUpperCase() === "CONTATO"; // Verifica se é o link "CONTATO"

  return (
    <NavLink
      to={to}
      className={({ isActive, isPending }) =>
        `${styles.link} ${isPending ? styles.pending : ''} ${isActive ? styles.active : ''} ${isContato ? styles.contato : ''}`
      }
    >
      {label}
    </NavLink>
  );
}

export default MenuLink;
