import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import SobreMim from './pages/sobreMim';
import Projetos from './pages/Projetos';
import Blog from './pages/blog';
import Menu from './componentes/Menu/Menu';
import PaginaPadrao from './pages/PaginaPadrao/index';

function App() {
  const location = useLocation();

  // Função para definir o background baseado na rota
  const getBackgroundImage = () => {
    switch (location.pathname) {
      case "/":
        return "url('/path/to/home-background.jpg')";
      case "/projetos":
        return "url('/path/to/projetos-background.jpg')";
      case "/blog":
        return "url('/path/to/blog-background.jpg')";
      default:
        return "url('/path/to/default-background.jpg')";
    }
  };

  return (
    <div style={{ backgroundImage: getBackgroundImage(), backgroundSize: 'cover', height: '100vh' }}>
      <Router>
        <Menu />
        <Routes>
          <Route path="/" element={<PaginaPadrao />} >
            <Route index element={<SobreMim />} />
            <Route path="/projetos" element={<Projetos />} />
            <Route path="/blog" element={<Blog />} />
          </Route>
          <Route path="*" element={<h1>Not Found</h1>} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
