import Apresentacao from "../../componentes/sobreApresentacao/apresentacao";
import Video from "../../componentes/sobreVideo/video";
import Resumo from "../../componentes/sobreResumo/Resumo";
import Informacoes from "../../componentes/sobreInformacoes/informacoes";
import useScrollAnimation from '../../useScrollAnimation'; // Ajuste o caminho conforme necess치rio

export default function SobreMim() {
  const ref = useScrollAnimation(); // Ativa o Intersection Observer

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('animate-in');
          }
        });
      },
      { 
        threshold: 0.2, // Ajuste conforme necess치rio
        rootMargin: '0px 0px -50px 0px' // Dispara 50px antes do elemento entrar
      }
    );

    if (sectionRef.current) observer.observe(sectionRef.current);

    return () => {
      if (sectionRef.current) observer.unobserve(sectionRef.current);
    };
  }, []);
  
  return (
    <section ref={ref} className="fade-up opacity-0 transition-all duration-500">
      <Apresentacao />
      <Video />
      <Resumo />
      <Informacoes />
    </section>
  );
}
