import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import styles from '../Menu/Menu.module.css'; // Importação do CSS Module
import Logo from '../img/logo.svg';
import MenuLink from '../MenuLink'; // Importação do componente MenuLink
import translations from '../../translations/translations'; // Importação das traduções
import 'f'; // Importação do CSS das bandeiras

function Header() {
  const location = useLocation(); // Obtém a rota atual
  const [isScrolled, setIsScrolled] = useState(false);
  const [language, setLanguage] = useState(localStorage.getItem('language') || 'pt'); // Estado para armazenar o idioma

  // Função para mudar o idioma
  const handleLanguageChange = (event) => {
    const selectedLanguage = event.target.value;
    setLanguage(selectedLanguage);
    localStorage.setItem('language', selectedLanguage); // Salva o idioma no localStorage
  };

  // Efeito para detectar o scroll
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`${styles.header} ${isScrolled ? styles.scrolled : ''}`}>
      <div className={styles.logo}>
        <img src={Logo} alt="Logo" />
      </div>
      <nav className={styles.navbar}>
        <ul>
          <li>
            <MenuLink 
              to="/" 
              label={translations[language].sobre}
              isActive={location.pathname === '/'} 
            />
          </li>
          <li>
            <MenuLink 
              to="/projetos" 
              label={translations[language].projetos}
              isActive={location.pathname === '/projetos'} 
            />
          </li>
          <li>
            <MenuLink 
              to="/blog" 
              label={translations[language].blog} 
              isActive={location.pathname === '/blog'} 
            />
          </li>
          <li>
            <MenuLink 
              to="/contato" 
              label={translations[language].contato} 
              isActive={location.pathname === '/contato'}
            />
          </li>
        </ul>
      </nav>
      {/* Seletor de idioma com bandeiras */}
      <div className={styles.languageSelector}>
        <select value={language} onChange={handleLanguageChange}>
          <option value="pt">
            <span className="flag-icon flag-icon-pt"></span> PT
          </option>
          <option value="en">
            <span className="flag-icon flag-icon-us"></span> EN
          </option>
          <option value="es">
            <span className="flag-icon flag-icon-es"></span> ES
          </option>
        </select>
      </div>
    </header>
  );
}

export default Header;
