import { useState, useMemo } from "react";
import { Link } from "react-router-dom";
import projects from "../../json/projects.json";
import "../navegacaoProjetos/navegacaoProjetos.css";
import "../textsFormate/formate.css";

function NavegacaoProjetos() {
    const [categoriaAtiva, setCategoriaAtiva] = useState("Todos");

    // Extrai categorias únicas dos projetos
    const todasCategorias = useMemo(() => {
        const categoriasUnicas = Array.from(
            new Set(projects.flatMap(projeto => projeto.categorias))
        );
        return categoriasUnicas.sort();
    }, []);

    const handleFiltro = (categoria) => {
        setCategoriaAtiva(categoria);
    };

    const projetosFiltrados = useMemo(() => {
        return categoriaAtiva === "Todos" 
            ? projects 
            : projects.filter(projeto => 
                projeto.categorias.includes(categoriaAtiva)
            );
    }, [categoriaAtiva]);

    return (
        <div className="main-container">
            <div className="projetos-container">
                <nav className="projetos-nav">
                    {['Todos', ...todasCategorias].map((categoria) => (
                        <button 
                            key={categoria} 
                            className={`projetos-nav-btn ${categoriaAtiva === categoria ? 'ativo' : ''}`}
                            onClick={() => handleFiltro(categoria)}
                        >
                            {categoria}
                        </button>
                    ))}
                </nav>

                <div className="cards-container">
                    {projetosFiltrados.map((projeto) => (
                        <Link 
                            to={`/projetos/${projeto.id}`} 
                            key={projeto.id}
                            className="projeto-card"
                            style={{
                                backgroundImage: `url(/assets/posts/${projeto.id}/projeto.png)`,
                                backgroundSize: 'cover',
                                backgroundPosition: 'center',
                            }}
                        >
                            <div className="card-content">
                                <h3 className="titulo-card">{projeto.nome}</h3>
                                <div className="categorias-container">
                                    {projeto.categorias.map((categoria, index) => (
                                        <span key={index} className="categoria-badge">
                                            {categoria}
                                        </span>
                                    ))}
                                </div>
                                <p className="descricao-card">{projeto.descricao}</p>
                                <div className="tecnicas-container">
                                    {projeto.tecnologias.map((tech, index) => (
                                        <span key={index} className="tecnica-pill">
                                            {tech}
                                        </span>
                                    ))}
                                </div>
                            </div>
                        </Link>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default NavegacaoProjetos;
