import React, { useState } from 'react';
import styles from '../Menu/Menu.module.css'; // Importação correta do CSS Module
import { Link, useLocation } from 'react-router-dom'; // Importando Link
import Logo from '../img/logo.svg';

function Header() {

const localizacao = useLocation();

  return (
    <header className={styles.header}>
      <div className={styles.logo}>
        <img src={Logo} alt="Logo" />
      </div>
      <nav className={styles.navbar}>
        <ul>
          <li>
            <Link className={`
            ${styles.link}
            ${localizacao.pathname === "/" ? styles.active : ""}
            `} to="/">
              SOBRE
            `}
              SOBRE
            </Link>
          </li>
          <li>
            <Link
              to="/projetos"
              className={`${activeTab === "projetos" ? styles.active : ""}`}
              onClick={() => setActiveTab("projetos")}
            >
              PROJETOS
            </Link>
          </li>
          <li>
            <Link
              to="/blog"
              className={`${activeTab === "blog" ? styles.active : ""}`}
              onClick={() => setActiveTab("blog")}
            >
              BLOG
            </Link>
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
