document.addEventListener('DOMContentLoaded', function () {
    const botoesAno = document.querySelectorAll('.ano');
    const detalhesEducacao = document.querySelectorAll('.educacao-detalhes');

    // Define o primeiro ano como ativo por padrão
    if (botoesAno.length > 0 && detalhesEducacao.length > 0) {
        botoesAno[0].classList.add('active');
        detalhesEducacao[0].style.display = 'flex';
    }

    botoesAno.forEach((botao, index) => {
        botao.addEventListener('click', () => {
            // Remove a classe ativa de todos os botões e esconde os detalhes
            botoesAno.forEach(btn => btn.classList.remove('active'));
            detalhesEducacao.forEach(det => det.style.display = 'none');

            // Adiciona a classe ativa ao botão clicado e mostra os detalhes correspondentes
            botao.classList.add('active');
            detalhesEducacao[index].style.display = 'flex';
        });
    });
});
