import '../textsFormate/formate.css';
import projects from '../../json/projects.json';
import './style.css';

export default function ProjetoPagina() {
    return (
        <div className="projeto-container">
            {/* Menu lateral */}
            <div className="projeto-menu">
                <p>Categoria</p>
                <ul>
                    <li>Hackathons</li>
                    <li>Hackathons</li>
                    <li>Hackathons</li>
                    <li>Hackathons</li>
                    <li>Hackathons</li>
                </ul>
            </div>

            {/* Conteúdo principal */}
            <div className="projeto-conteudo">
                <div className="projeto-conteudo-container">
                    {/* Informações do projeto */}
                    <div className="projeto-conteudo-info">
                        {/* Substituir caminho dinâmico da imagem se necessário */}
                        <img src="../../assets/posts/1/projeto.png" alt="Imagem do projeto" />
                        <div className="info">
                            <div className="project-category">Aplicativo</div>
                            <a className="project-button">
                                <i className="fi fi-rr-share"></i> Share
                            </a>
                            {/* TODO: Adicionar funcionalidade para salvar em PDF ou compartilhar o link */}
                        </div>
                    </div>

                    {/* Texto do projeto */}
                    <div className="projeto-conteudo-texto">
                        <p>Informações do Projeto:</p>
                        <p>Talking Care é uma plataforma inovadora voltada para melhorar a comunicação entre pacientes e profissionais da saúde. A solução utiliza inteligência artificial para automatizar lembretes de consultas, fornecer informações personalizadas sobre tratamentos e permitir que os usuários tirem dúvidas em tempo real com assistentes virtuais.O objetivo do Talking Care é tornar o cuidado com a saúde mais acessível, eficiente e integrado, promovendo uma experiência centrada no paciente. O projeto foi desenvolvido durante um hackathon, focando na integração com dispositivos móveis e compatibilidade com wearables para monitoramento contínuo.</p>
                    </div>
                    <div className="divisoria"></div>
                </div>
            </div>

            {/* Lista adicional (ajustar conforme necessidade) */}
            <div className="projeto-lista">
                <p>afjsj</p>
            </div>
        </div>
    );
}
