import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import styles from "../Menu/Menu.module.css"; // Importação do CSS Module
import Logo from "../img/logo.svg";
import MenuLink from "../MenuLink";
import translations from "../../translations/translations";
import FlagPT from "../img/flags/brasil.png";
import FlagUS from "../img/flags/estados-unidos.png";
import FlagES from "../img/flags/spain.png";

function Header() {
  const location = useLocation();
  const [isScrolled, setIsScrolled] = useState(false);
  const [language, setLanguage] = useState(
    localStorage.getItem("language") || "pt"
  );
  const [isSelectorOpen, setIsSelectorOpen] = useState(false); // Estado para mostrar/esconder seletor

  const languages = {
    pt: { label: "PT", flag: FlagPT },
    en: { label: "EN", flag: FlagUS },
    es: { label: "ES", flag: FlagES },
  };

  const handleLanguageChange = (event) => {
    const selectedLanguage = event.target.value;
    setLanguage(selectedLanguage);
    localStorage.setItem("language", selectedLanguage);
    setIsSelectorOpen(false); // Fecha o seletor após escolher um idioma
  };

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header className={`${styles.header} ${isScrolled ? styles.scrolled : ""}`}>
      <div className={styles.logo}>
        <img src={Logo} alt="Logo" />
      </div>

      <nav className={styles.navbar}>
        <ul>
          <li>
            <MenuLink
              to="/"
              label={translations[language].sobre}
              isActive={location.pathname === "/"}
            />
          </li>
          <li>
            <MenuLink
              to="/projetos"
              label={translations[language].projetos}
              isActive={location.pathname === "/projetos"}
            />
          </li>
          <li>
            <MenuLink
              to="/contato"
              label={translations[language].contato}
              isActive={location.pathname === "/contato"}
            />
          </li>
        </ul>

        {/* Seletor de idioma */}
        <div
          className={styles.languageSelector}
          onClick={() => setIsSelectorOpen(!isSelectorOpen)} // Alterna a visibilidade ao clicar
          style={{ cursor: "pointer" }}
        >
          <img
            src={languages[language].flag}
            style={{ width: "40px", height: "40px" }}
          />
          {isSelectorOpen && ( // Só exibe o select quando `isSelectorOpen` for true
            <select value={language} onChange={handleLanguageChange} autoFocus>
              {Object.keys(languages).map((key) => (
                <option key={key} value={key}>
                  {languages[key].label}
                </option>
              ))}
            </select>
          )}
        </div>
      </nav>
    </header>
  );
}

export default Header;
