import './projetos-inicio.css';
import '../textsFormate/formate.css';
import { useLocation } from 'react-router-dom';
import { useEffect, useState } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';
import Notificacao from '../Notificacao/Notificacao'; // ajuste o caminho conforme necessário
import IconClipboard from '../../assets/clipboard.svg'; // opcional, use a imagem que preferir

function ProjetosInicio() {
  const location = useLocation();
  const [notificacaoVisivel, setNotificacaoVisivel] = useState(false);

  useEffect(() => {
    AOS.init({
      duration: 1000,
      once: true,
    });
  }, []);

  const getTitles = () => {
    switch(location.pathname) {
      case '/projetos':
        return { subtitle: 'Meus', title: 'Projetos' };
      case '/contato':
        return { subtitle: 'Entre em', title: 'Contato' };
      default:
        return { subtitle: '', title: '' };
    }
  };

  const { subtitle, title } = getTitles();

  const handleCopiarTexto = () => {
    navigator.clipboard.writeText('Texto de exemplo copiado!');
    setNotificacaoVisivel(true);
    setTimeout(() => setNotificacaoVisivel(false), 3000); // Auto fecha a notificação após 3 segundos
  };

  return (
    <>
      <div className="pagina-banner projetos">
        <div className="apresentacao-projetos">
          <h5 className="themeWhite" data-aos="fade" data-aos-delay="100">{subtitle}</h5>
          <h1 className="themeOrange" data-aos="fade" data-aos-delay="300">{title}</h1>
        </div>

        {/* Botão de exemplo pra disparar a notificação */}
        <button onClick={handleCopiarTexto} className="btnCopiar">
          Copiar texto
        </button>
      </div>

      {/* Notificação visível */}
      {notificacaoVisivel && (
        <Notificacao
          visivel={notificacaoVisivel}
          onFechar={() => setNotificacaoVisivel(false)}
          mensagem="Email copiado com sucesso!"  // Alterar a mensagem conforme desejado
          imagem={IconClipboard} // A imagem do ícone de clipboard
        />
      )}
    </>
  );
}

export default ProjetosInicio;
