import React from 'react';
import { NavLink } from 'react-router-dom';
import styles from './menuLink.module.css';

function MenuLink({ to, label, className }) {
  return (
    <NavLink
      to={to}
      className={({ isActive }) => {
        let baseClass = className;
        // Verifica se é uma página com fundo diferente
        if (['Contato', 'Projetos'].includes(label)) {
          return `${baseClass} ${isActive ? styles.contactActive : ''}`;
        }
        return `${baseClass} ${isActive ? styles.active : ''}`;
      }}
    >
      {label}
    </NavLink>
  );
}

export default MenuLink;
