import { useState } from 'react';
import educacao from './educacao.json'; // Certifique-se de que o caminho está correto

const Educacao = () => {
    const [anoSelecionado, setAnoSelecionado] = useState(2025);

    const cursosFiltrados = educacao.filter(curso => new Date(curso.initialDate).getFullYear() === anoSelecionado);

    return (
        <div className='educacao-background'>
            <div className="educacao">
                <p className="subtitulo themePurple">Educação</p>
                <div className='educacao-opcoes'>
                    <ul>
                        {[2025, 2024, 2023, 2022].map(ano => (
                            <li 
                                key={ano} 
                                className={`ano ${ano === anoSelecionado ? 'ativo' : ''}`} 
                                onClick={() => setAnoSelecionado(ano)}
                            >
                                {ano}
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="educacao-detalhes">
                    {cursosFiltrados.length > 0 ? (
                        cursosFiltrados.map(curso => (
                            <article key={curso.id}>
                                <h3>{curso.course}</h3>
                                <span>{curso.initialDate} - {curso.endDate}</span>
                                <p>{curso.description}</p>
                            </article>
                        ))
                    ) : (
                        <p>Não há cursos cadastrados para este ano.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Educacao;
